mapper.select:
SELECT 

FROM t_group a

WHERE a.id = #{id}

SELECT 

FROM t_group a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
