mapper.update:
DELETE FROM group_user


		WHERE id = #{id}


		WHERE group_id = #{group.id}

UPDATE group_user SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE group_id = #{group.id}
